{
    "$schema": "http://json-schema.org/draft-07/schema#",
    "title": "BadgeIn",
    "type": "object",
    "properties": {
        "recordid": {
            "type": "string",
            "format": "uuid",
            "description": "Unique identifier for the record."
        },
        "door": {
            "type": "string",
            "description": "ID of the door where the access event occurred. This is made up of a building ID, a floor number, and a door ID - separated by hyphens."
        },
        "employee": {
            "type": "string",
            "description": "Employee's username."
        },
        "badgetime": {
            "type": "string",
            "pattern": "^\\d{4}-\\d{2}-\\d{2} \\d{2}:\\d{2}:\\d{2}\\.\\d{1,3}$",
            "description": "Timestamp when the badge was used, formatted as 'YYYY-MM-DD HH:MM:SS.sss'."
        }
    },
    "required": [
        "recordid",
        "door",
        "employee",
        "badgetime"
    ],
    "additionalProperties": false
}

{
    "$schema": "http://json-schema.org/draft-07/schema#",
    "title": "SensorReading",
    "type": "object",
    "properties": {
        "sensortime": {
            "type": "string",
            "pattern": "^(Mon|Tue|Wed|Thu|Fri|Sat|Sun)\\s(Jan|Feb|Mar|Apr|May|Jun|Jul|Aug|Sep|Oct|Nov|Dec)\\s\\d{2}\\s\\d{2}:\\d{2}:\\d{2}\\s(GMT|UTC)\\s\\d{4}$",
            "description": "Time that the sensor reading was recorded"
        },
        "sensorid": {
            "type": "string",
            "pattern": "^[A-Z]{2}-\\d{1}-\\d{2}$",
            "description": "Unique id for the sensor that captured the reading. This is made up of a building ID, a floor number, and a sensor ID - separated by hyphens."
        },
        "temperature": {
            "type": "number",
            "minimum": 18.0,
            "maximum": 30.0,
            "description": "Temperature recorded by the sensor, in degrees Celsius"
        },
        "humidity": {
            "type": "integer",
            "minimum": 0,
            "maximum": 100,
            "description": "Humidity reading, recorded as a percentage"
        }
    },
    "required": [
        "sensortime",
        "sensorid",
        "temperature",
        "humidity"
    ],
    "additionalProperties": false
}

